/**
 * Analyse la polarité d'une URL en scannant la page et en traitant une par une les phrase
 * contenant le nom d'un candidat
 *
 * @param currentUrl
 * @return
 * @throws IOException
 */
Classification evaluateURLPolarite(List<String> urls) throws IOException {
  StringBuffer toAnalyse = new StringBuffer("");
  for (String currentUrl : urls) {
    try {
      String[] splittedArticle = null;
      Document doc = Jsoup.connect(currentUrl).timeout(2500).get();
      if (doc.body() != null && !doc.body().text().isEmpty()) {
        splittedArticle = (doc.body().text()).split("\\.");
        for (String phrase : splittedArticle) {
          for (Candidat candidat : candidats) {
            if (phrase.toLowerCase().indexOf(candidat.getCandidatName().toString().toLowerCase())
                != -1) {
              toAnalyse.append(phrase);
            }
          }
        }
      }
    } catch (IOException e) {
      LOG.error("ERREUR EN SCANNANT URL : " + currentUrl);
    } catch (Exception e) {
      LOG.error("EXCEPTION");
    }
  }
  if (!toAnalyse.equals("")) {
    return evaluate(toAnalyse.toString());
  } else {
    return null;
  }
}

@Override
public String GetUrl() throws IOException, IncorrectDataFormat {
  if (preferences.getBoolean("tagPhotoEnable", false)) {
    String tag = preferences.getString("tagPhotoValue", "");
    if (tag.length() > 0) {
      return GetUrlByTag(tag);
    }
  }

  String imageUrl = null;
  String url = "https://www.flickr.com/explore/";

  try {
    // чистка памяти
    System.gc();

    Document doc = Jsoup.connect(url).get();

    Element table = doc.select("div[class=ju photo-display-container clearfix]").first();
    if (table == null) {
      throw new IncorrectDataFormat(doc.ownText());
    }

    Element rows = table.select("div[class=row row-]").first();
    if (rows == null) {
      throw new IncorrectDataFormat(doc.ownText());
    }

    Element item = rows.select("div[class=photo-display-item]").first();
    if (item == null) {
      throw new IncorrectDataFormat(doc.ownText());
    }

    Element photo = item.select("span[class=photo_container pc_ju]").first();
    if (photo == null) {
      throw new IncorrectDataFormat(doc.ownText());
    }

    Element photo_a = photo.select("a[class=rapidnofollow photo-click]").first();
    if (photo_a == null) {
      throw new IncorrectDataFormat(doc.ownText());
    }

    Element photo_img = photo_a.select("img").first();
    if (photo_img == null) {
      throw new IncorrectDataFormat(doc.ownText());
    }

    imageUrl = photo_img.attr("data-defer-src");
    imageUrl = imageUrl.replace(".jpg", "_b.jpg");
  } catch (OutOfMemoryError e) {
    // try{
    // BugSenseHandler.sendExceptionMessage("Flickr.GetUrl", url, new
    // hram.android.PhotoOfTheDay.Exceptions.OutOfMemoryError(e.getMessage()));
    // }catch (Exception e2) {}
    return null;
  }

  return imageUrl;
}

