public List getResultsByTitle(@PathParam("query") String query) throws IOException {
  String URL = host + query;
  List results = new ArrayList();
  Document doc = Jsoup.connect(URL).get();
  for (Iterator<Element> it = doc.getElementsByTag("marc:record").iterator();
      it.hasNext(); ) { // Splits the result into seperate books
    Element e = it.next();
    for (Iterator<Element> it1 = e.getElementsByTag("marc:datafield").iterator();
        it1.hasNext(); ) { // Splits the result into seperate infofields
      Element e1 = it1.next();
      if (e1.toString()
          .contains(
              "245")) { // Separates the info from field 245, which contains the title and the
                        // name of the author
        String title = null;
        String author = null;
        for (Iterator<Element> it2 = e1.getElementsByTag("marc:subfield").iterator();
            it2.hasNext(); ) { // Splits the result into seperate sub-infofields
          Element e2 = it2.next();
          if (e2.toString().contains("code=\"a\"")) {
            String[] array = e2.toString().split("\n");
            title = array[1].substring(1);
            title =
                title
                    .replaceAll("&Oslash;", "Ø")
                    .replaceAll("&oslash;", "ø")
                    .replaceAll("&aelig;", "æ")
                    .replaceAll("&AElig;", "Æ")
                    .replace("&aring;", "å")
                    .replace("&aring;", "Å");
          } else if (e2.toString().contains("code=\"b\"")) {
            String[] array = e2.toString().split("\n");
            title = title + array[1];
            title =
                title
                    .replaceAll("&Oslash;", "Ø")
                    .replaceAll("&oslash;", "ø")
                    .replaceAll("&aelig;", "æ")
                    .replaceAll("&AElig;", "Æ")
                    .replace("&aring;", "å")
                    .replace("&aring;", "Å");
          } else if (e2.toString().contains("code=\"c\"")) {
            String[] array = e2.toString().split("\n");
            author = array[1].substring(1);
            author =
                author
                    .replaceAll("&Oslash;", "Ø")
                    .replaceAll("&oslash;", "ø")
                    .replaceAll("&aelig;", "æ")
                    .replaceAll("&AElig;", "Æ")
                    .replace("&aring;", "å")
                    .replace("&aring;", "Å");
          }
        }
        results.add(new Book(title, author));
      }
    }
  }
  return results;
}

