@Override
@BotCommand("fed")
public String find(@Option(name = "키워드", help = "검색할 단어") String keyword) {
  String result = "";

  if (keyword.equals("")) {
    result = "키워드를 지정하지 않았습니다";
    return result;
  }

  try {

    String url =
        "http://rpmfind.net/linux/rpm2html/search.php?query=" + keyword + "&submit=Search";

    Connection conn = Jsoup.connect(url);
    conn.timeout(5000);

    Elements tables = conn.get().select("table");

    if (tables.size() < 2) {
      result = "[Fedora] 결과가 없습니다";
      return result;
    }

    System.out.println(tables.get(1).select("tbody>tr").toString());
    Iterator<Element> row = tables.get(1).select("tbody>tr").iterator();
    boolean firstrow = false;

    String pkgname = "";
    String description = "";
    String dist = "";
    String NamenVer = "";

    while (row.hasNext()) {
      if (!firstrow) {
        row.next(); // dummy
        firstrow = true;
        continue;
      }

      Elements e = row.next().select("td");
      System.out.println(e);
      pkgname = e.get(0).select("a").get(0).text();
      description = e.get(1).text();
      dist = e.get(2).text();

      if (dist.split("\\s")[0].equals("Fedora")) {
        int pkg_ver;
        try {
          pkg_ver = Integer.parseInt(dist.split("\\s")[1]);
        } catch (Exception ee) {
          continue;
        }

        description += " in the Fedora " + pkg_ver;

        String[] spl = pkgname.split("\\.");
        int i = 0;

        // exclude .fcxx.html string
        for (; !spl[i].contains("fc"); i++) {
          if (i != 0) NamenVer += ".";
          NamenVer += spl[i];
        }

        // split pkgname and version
        spl = NamenVer.split("\\-");
        String name = spl[0];
        String ver = "";
        i = 1;

        while (!(spl[i].charAt(0) >= '0' && spl[i].charAt(0) <= '9')) {
          name += "-" + spl[i++];
        }

        int initial_veridx = i;
        while (i < spl.length) {
          if (initial_veridx != i) ver += "-";
          ver += spl[i++];
        }

        NamenVer = name + " " + ver;

        break;
      }
    }

    // need to give result into result.
    if (!dist.contains("Fedora")) {
      result = "[Fedora] 결과가 없습니다";
    } else {
      result = NamenVer + " : " + description;
    }

  } catch (Exception e) {
    logger.error(e.getMessage(), e);
    result = "오류: " + e.getMessage();
  }

  return result;
}

@Override
public void onMessage(MessageEvent event) {
  String[] segs = event.getMessageSegments();

  if (ArrayUtils.contains(commands, segs[0])) {
    if (segs.length == 1 || StringUtils.isBlank(segs[1])) {
      event.respond(getHelp());
    } else {
      String type = segs[0];
      String script = segs[1];

      switch (type) {
        case "!scala":
          type = "scala";
          break;
        case "!java":
          type = "java";
          break;
        case "!js":
        case "!javascript":
          type = "javascript";
          break;
        case "!ruby":
          type = "ruby";
          break;
        case "!py":
        case "!python":
          type = "python";
          break;
        case "!groovy":
          type = "groovy";
          break;
      }

      try {
        String result =
            Jsoup.connect(serverAddress)
                .data("type", type)
                .data("script", script)
                .method(Method.POST)
                .execute()
                .body();

        if (StringUtils.isBlank(result)) {
          result = "결과가 없습니다.";
        } else {
          result = result.replace("\n", "");
        }

        event.respond(result);
      } catch (HttpStatusException e) {
        logger.warn(e.getMessage(), e);
        event.respond(e.getMessage());
      } catch (IOException e) {
        logger.warn(e.getMessage(), e);
        event.respond(e.getMessage());
      }
    }
  }
}

/**
 * Entry point for parsing HTML-based LaBRI events
 *
 * @param category the containing category of the feed to parse
 * @param feed the HTML feed to parse
 * @param existingEvents list of previously-parsed events
 * @throws IOException
 * @throws ParseException
 */
public void parseLabriSection(Category category, Feed feed, ArrayList<Event> existingEvents)
    throws IOException, ParseException {
  int months = CampusUB1App.persistence.getUpcomingEventsRange();
  int timestamp = MONTH_SECONDS * months;
  for (int i = 0; i < months; i++) {
    Long current = System.currentTimeMillis() / 1000 + timestamp;
    Connection.Response res =
        Jsoup.connect("http://www.labri.fr/public/actu/accueil.php")
            .userAgent("Mozilla")
            .data("choix_intervalle", "mois")
            .data("mois", current.toString())
            .data(feed.getUrl(), "1")
            .referrer("http://www.labri.fr/public/actu/index.php")
            .method(Method.POST)
            .execute();
    parseDocument(res.parse(), category, existingEvents);
    timestamp -= MONTH_SECONDS;
  }
  mParsedBuildDates.add(new Date());
}

