            course.description = description.substring(0, description.indexOf("<br />")).trim();
            description =
                description
                    .substring(description.indexOf("<br />") + 6, description.length())
                    .trim();
          }
        } else if (description.startsWith("<br />")) {
          description = description.substring(6).trim();
        } else {
          System.out.print("ERROR: CANNOT PARSE TEXT ");
          System.out.println(description);
        }
      }
      if (course.description == null || course.description.length() == 0) {
        course.description = course.title;
      }
      // if(course.hours == null || course.hours.length() == 0){
      // course.hours = "Variable";
      // }
      course.ugrad = true;
      course.id = count++;
      if (!course.verify()) {
        System.out.println("Problems!");
      }
      courselist.add(course);
    }
    System.out.println("Page " + page + " complete! " + count + " courses processed so far...");
    page++;
  }
}

/**
 * @param user
 * @throws TRCardManagerLoginException
 * @throws ClientProtocolException
 * @throws IOException
 */
public void getCookieLogin(UserDao user)
    throws TRCardManagerLoginException, ClientProtocolException, IOException {
  Map<String, String> postMap = new HashMap<String, String>();
  postMap.put(LOGIN_FIELD_USER, user.getEmail());
  postMap.put(LOGIN_FIELD_PASSW, user.getPassword());
  postMap.put(LOGIN_FIELD_TYPE, TYPE_PARAMETER);
  postMap.put(LANG_PARAMETER, LANG_PARAMETER_VALUE);

  Response response = Jsoup.connect(URL_BASE + URL_LOGIN).data(postMap).execute();
  if (response != null) {
    String responseString = response.body();
    if (LOGIN_RESPONSE_OK.equals(responseString)) {
      String cookieValue = response.cookie(COOKIE_NAME);
      user.setCookieValue(cookieValue);
    } else {
      throw new TRCardManagerLoginException();
    }
  } else {
    throw new TRCardManagerLoginException();
  }
}

