private Response postLoginForm() throws Exception {

  // Prepare POST url (spoiler: contains variables!)
  String url =
      "https://www.gotlib.goteborg.se/iii/cas/login;jsessionid="
          + sessionVariables.get("JSESSIONID")
          + "?service=http%3A%2F%2Fencore.got"
          + "lib.goteborg.se%3A80%2Fiii%2Fencore%2Fj_acegi_cas_security_c"
          + "heck&lang="
          + sessionVariables.get(
              "org.springframework.web.se" + "rvlet.i18n.CookieLocaleResolver.LOCALE");

  // Prepare POST data.
  @SuppressWarnings("serial")
  Map<String, String> postData =
      new HashMap<String, String>() {
        {
          put("name", credentials.name);
          put("code", credentials.card);
          put("pin", credentials.pin);
          put("lt", sessionVariables.get("lt"));
          put("_eventId", "submit");
        }
      };

  // Send POST request and save response.
  Response response =
      Jsoup.connect(url)
          .method(Method.POST)
          .timeout(Backend.CONNECTION_TIMEOUT)
          .data(postData)
          .cookies(sessionCookies)
          .execute();

  // These new cookies are all we'll need.
  sessionCookies = response.cookies();

  // Tests.
  if ((sessionVariables.get("lt") == null)
      || (sessionCookies.get("JSESSIONID") == null)
      || (sessionCookies.get("III_SESSION_ID") == null)) {
    throw new Exception("missing cookies/variables.");
  }
  return response;
}

public String find(String url, String commandName) {
  String result = "";
  boolean hasExacthits = false;

  try {

    Connection conn = Jsoup.connect(url);
    conn.timeout(20000);
    Document doc = conn.get();

    if (doc.select("#psearchres").size() == 0) {
      result = "[" + commandName + "] 결과가 없습니다";
      return result;
    }

    Elements hits = doc.select("#psearchres").select("h2");
    int hsize = hits.size();

    if (hsize == 0) result = "[" + commandName + "] 결과가 없습니다";
    for (int i = 0; i < hsize; i++) {
      if (hits.get(i).text().equals("Exact hits")) {
        hasExacthits = true;
        break;
      }
    }
    if (!hasExacthits) {
      result = "[" + commandName + "] 결과가 없습니다";
      return result;
    }

    String pkgname = doc.select("#psearchres").select("h3").get(0).text().split("\\s")[1];

    Elements exactHits = doc.select("#psearchres").select("ul").get(0).select("li");
    int elemCnt = exactHits.size();
    Element latestElement = exactHits.get(elemCnt - 1);
    String description = latestElement.toString().split("<br>")[0].split("\\:")[1].trim();

    result = "[" + commandName + "] \u0002" + pkgname + "\u0002 - " + description + ", ";
    result += parseVersionInfo(doc) + ".";
  } catch (Exception e) {
    logger.error(e.getMessage(), e);
    result = "오류: " + e.getMessage();
  }

  return result;
}

