private void parseContentsFromLink(SyndEntry rssItem, Article article) {
  String rssLink = rssItem.getLink();
  if (!rssLink.contains("daum")) {
    return;
  }

  Document doc;
  try {
    doc = Jsoup.connect(rssLink).timeout(6000).get();
  } catch (IOException e) {
    // LOG.error(rssLink + "\n", e);
    return;
  }

  String contentId = "#harmonyContainer";
  Elements contentsArea = doc.select(contentId);

  // remove the .image tag because the title is existed again as the caption of the img.
  contentsArea.select(".txt_caption").remove();

  article.setContents(
      ContentsAnalysisService.removeInvalidWordsForKR(contentsArea.text().trim()));

  // extract the img link ////////////////////////////////////////////////////////
  if (!rssItem.getEnclosures().isEmpty()) {
    SyndEnclosure enclosure = (SyndEnclosure) (rssItem.getEnclosures().get(0));
    article.setImg(enclosure.getUrl());
  }
}

private String getInfo(String uri) {
  String result = null;
  Response response;

  try {
    // 일부 사이트에서는 User Agent가 있어야 접근을 허용
    response = Jsoup.connect(uri).userAgent(USER_AGENT).execute();
  } catch (UnsupportedMimeTypeException e) {
    return enablePrintContentType ? "[링크 형식] " + e.getMimeType() : null;
  } catch (IOException e) {
    logger.warn(e.getMessage(), e);
    return null;
  }

  String contentType = response.contentType();

  // 일단 title이 나오는지 시도해 본다.
  try {
    Document document = response.parse();
    String title = document.title();

    if (StringUtils.isBlank(title)) throw new IOException();

    title = title.trim().replaceAll("(\\s){1,}", " ");

    // 몇몇 사이트에 대한 처리
    String stitle = getSiteSpecificTitle(uri, document);
    if (stitle == null) result = "[링크 제목] " + title;
    else result = "[링크 제목] " + stitle + " | " + title;
  } catch (IOException e) {
    // parse 오류 또는 빈 title -- HTML의
    // 경우는 빈 제목이라도 표시하고 아니면
    // content type 표시로 패스
    if (contentType.startsWith("text/html")) result = "[링크 제목]";
  }

  if (result == null && enablePrintContentType) {
    result = "[링크 형식] " + contentType;
  }

  return result;
}

