    removedList.clear();
    if (!map.containsKey(name.getCompanyName()) && name.getDetailList().size() > 0) {
      // System.out.println("PUT INTO MAP : "+name.getCompanyName().replace('.', '#'));
      map.put(name.getCompanyName().replace('.', '#'), name);
    }
  }
  return map;
}

/**
 * Returns an empty string if no match is found. WARNING: MAGIC NUMBERS Fag/Course = 3
 * Klasse/Program = 5 Lærer/Teacher/Person = 6 Rom/Room = 7
 *
 * @param query
 * @param int
 * @return
 */
public String getObjectCodeFromQuery(String query, int type) {
  String searchURL =
      TIMEEDIT_HIALS_URL
          + TIMEDIT_PARAM_SEARCH_TYPE
          + "="
          + type
          + "&"
          + TIMEDIT_PARAM_SEARCH
          + "="
          + query;
  System.out.println("searchURL:" + searchURL);
  Document doc = null;
  try {
    doc = Jsoup.connect(searchURL).get();
  } catch (IOException ex) {
    Logger.getLogger(TimeEditService.class.getName()).log(Level.SEVERE, null, ex);
  }
  Elements elements = doc.select("a");
  System.out.println("Elements size: " + elements.size());
  // Regex is just a very lazy guess that none of the links contains six digits in a row.
  // Should be expanded to match the javascript function "addObject". DONE
  Pattern pattern = Pattern.compile("javascript:addObject\\((\\d{6}|\\d{7})\\)");
  Matcher matcher;
  for (Element e : elements) {
    String currentString = e.attr("href");
    System.out.println("currentString: " + currentString);
    if (currentString != null && !currentString.isEmpty()) {
      matcher = pattern.matcher(currentString);
      if (matcher.find()) {
        System.out.println("matcher: " + matcher.group());
        String retVal = matcher.group().replace("javascript:addObject(", "");
        retVal = retVal.replace(")", "");
        return retVal;
      }
    }
  }
  return "";
}

