public static void Filter_By_Wiki(ConcurrentHashMap<String, Integer> phrases) {
  for (String key : phrases.keySet()) {
    // if (phrases.get(key) == 1)
    // continue;
    System.out.print(".");
    String[] words = key.split(" ");
    String searchterm = "";
    for (int i = 0; i < words.length; ++i) {
      searchterm += words[i];
      if (i < words.length - 1) searchterm += "_";
    }
    Response wiki_response = null;
    Response wiki_responseWithExtraS = null;
    try {
      System.out.print("/");
      wiki_response =
          Jsoup.connect("http://en.wikipedia.org/wiki/" + searchterm)
              .timeout(0)
              .ignoreHttpErrors(true)
              .execute();
      wiki_responseWithExtraS =
          Jsoup.connect("http://en.wikipedia.org/wiki/" + searchterm + "s")
              .timeout(0)
              .ignoreHttpErrors(true)
              .execute();
      // System.err.println(key+" : "+wiki_response.statusCode()+" :
      // "+wiki_response2.statusCode());
    } catch (IOException e) {
      // TODO Auto-generated catch block
      e.printStackTrace();
    }
    int status = wiki_response.statusCode();
    int statusWithExtraS = wiki_responseWithExtraS.statusCode();
    if (status != HTTP_OKAY && statusWithExtraS != HTTP_OKAY) {
      phrases.remove(key);
    } else if (status != HTTP_OKAY && statusWithExtraS == HTTP_OKAY) {
      int tempVal = phrases.get(key);
      phrases.remove(key);
      key = key.trim() + "s";
      phrases.put(key, tempVal);
    } else if (status == HTTP_OKAY && statusWithExtraS == HTTP_OKAY) {
      int tempVal = phrases.get(key);
      phrases.remove(key);
      key = key.trim() + "s";
      phrases.put(key, tempVal);
    }
  }
}

