      "http://sru.bibsys.no/search/biblio?version=1.2&operation=searchRetrieve&startRecord=1&maximumRecords=10&query=bs.bibkode=xb%20AND%20bs.tittel="
          + query;
  Document doc = Jsoup.connect(host).get();
  // doc = Jsoup.parse(doc.html(), "", Parser.xmlParser());
  for (Iterator<Element> it = doc.getElementsByTag("marc:record").iterator();
      it.hasNext(); ) { // Splits the result into seperate books
    Element e = it.next();
    for (Iterator<Element> it1 = e.getElementsByTag("marc:datafield").iterator();
        it1.hasNext(); ) { // Splits the result into seperate infofields
      Element e1 = it1.next();
      if (e1.toString()
          .contains(
              "245")) { // Separates the info from field 245, which contains the title and the
                        // name of the author
        String[] array = e1.toString().split("\n");
        System.out.println("title: " + array[2].substring(3));
        System.out.println("Author: " + array[5].substring(3));
      }
    }
  }
}

public static String enToKr(String en) {

  Document doc;
  try {
    String q =
        "http://translate.google.com/translate_a/t?client=t&sl=en&tl=ko&hl=ko&ie=UTF-8&oe=UTF-8&prev=btn&ssel=3&tsel=6";

    String reqStr = en.replaceAll("“", "'");

    doc = Jsoup.connect(q).userAgent("Mozilla").data("q", reqStr).post();

    String ans = doc.text();

    int idx = ans.indexOf("]]");
    String tr = ans.substring(2, idx + 2);
    String[] arr = tr.split("],");

    StringBuilder sb = new StringBuilder();
    for (String sen : arr) {
      // LOG.debug("{}", sen);
      String[] arr2 = sen.split("\",\"");
      String s = arr2[0];

      // TODO: make it better... :-(
      String f =
          s.substring(2)
              .replaceAll(" \\.", ".")
              .replaceAll("\\\\\"", "\"")
              .replaceAll(" ,", ",")
              .replaceAll(" 's", "'s")
              .replaceAll(" ' ", " '");

      // LOG.debug("{}", f);
      sb.append(f);
    }

    return sb.toString();

  } catch (Exception e) {
    LOG.error("{}\n", en, e);
    return "";
  }
}
